async function calcTokenPrice(numTokens, tokName){
     if(walletConnected == false){
          await initWeb3();
     }
     //do work to calculate val

     await buyTokens(numTokens, "0.02", tokName);

}

function submitPurchase(){
     var numTokens = document.querySelector('#numTokens').value;
     var tokName = document.querySelector('#tokName').value;
     if (numTokens == 0){
          console.log(" no number selected");
          return;
     }
     if (tokName == "null"){
          console.log(" no name selected");
          return;
     }

     //if form submitted successfully, run the price and activate the transaction
     calcTokenPrice(numTokens, tokName);
     
}




